import { useState } from 'react';
import { motion } from 'framer-motion';
import { FiX } from 'react-icons/fi';
import Button from './reusable/Button';

const selectOptions = [
  'Web Application',
  'Mobile Application',
  'UI/UX Design',
  'Branding',
];

const HireMeModal = ({ onClose, onRequest }) => {
  const [messageSent, setMessageSent] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic to handle form submission
    // You can set the messageSent state to true after successful submission
    setMessageSent(true);
  };

  return (
    <motion.div
      // ... rest of your code ...
    >
      {/* ... rest of your code ... */}
            <div className="modal-body p-6 w-full h-full">
              {messageSent ? (
                <p className="text-green-500 mb-4">Message sent successfully!</p>
              ) : (
                <form
                  onSubmit={handleSubmit}
                  className="max-w-3xl m-4 text-left"
                >
                  {/* ... your form fields ... */}
                  <div className="mt-6 pb-4 sm:pb-1">
                    <span
                      onClick={onClose}
                      type="submit"
                      className="px-4
                        sm:px-6
                        py-2
                        sm:py-2.5
                        text-white
                        bg-indigo-500
                        hover:bg-indigo-600
                        rounded-md
                        focus:ring-1 focus:ring-indigo-900 duration-500"
                      aria-label="Submit Request"
                    >
                      <Button title="Send" />
                    </span>
                  </div>
                </form>
              )}
            </div>
            {/* ... rest of your code ... */}
      {/* ... rest of your code ... */}
    </motion.div>
  );
};

export default HireMeModal;
